    public class LKM_Hive_to_Pig extends LKMPigHive {

        private GeneratorContext generatorContext;
	    protected boolean useStagingTable

        public void setBinding( Binding binding ) {
           super.setBinding(binding);

           generatorContext =  binding.getVariable("generatorContext");

		   useStagingTable = false
		   binding.setVariable( "useStagingTable",  useStagingTable ) 
        }

        boolean isDirectLoad() {
          return physicalNode.getUpstreamConnectedNodes().get(0).isDataStorageNode();
        }

        void createStagingTable(SqlQuery inputQuery, PigScript pigScript) {

            List<ISelectItem> newSelectList = new ArrayList<ISelectItem>();
            List<StringExpression> newAliasList = new ArrayList<StringExpression>();
            List<PigField> pigFields = new ArrayList<PigField>();    

            StringBuilder strBuilder = new StringBuilder();
            def isFirst = true;

            PigLoadCmd loadCmd = (PigLoadCmd)pigScript.getLastCommand();

            List<MapPhysicalColumn> cols = physicalNode.getParentNode().getPhysicalColumns();                   
            for (MapPhysicalColumn col : cols) {
                 PigField pigField = loadCmd.createProjectorFieldForOutputAttribute(pigScript.getOutputSchema(), col.getReferencedAttribute());
                 pigFields.add(pigField);
                 newSelectList.add(new ArrayExpression(col, inputQuery));
                 newAliasList.add(new StringExpression(pigField.getName(),col));

                 if (isFirst) isFirst = false; 
                 else strBuilder.append(",").append(System.getProperty("line.separator")).append("        ");
                 strBuilder.append(pigField.getName()).append(" ").append(col.getDdlDataType()); 
            }

            pigScript.getOutputSchema().setFields(pigFields);

            SqlQuery result = inputQuery.clone();
            result.setSelectList(newSelectList);
            result.setAliasList(newAliasList);

            String stageTableName = generatorContext.getAPNodeCollTableAccessName(physicalNode);
            StagingTable stagingTable = new StagingTable(stageTableName, strBuilder.toString());
            HiveInsertStatement insertStmt = new HiveInsertStatement(stageTableName, strBuilder.toString(), result);
            binding.setVariable("stagingTable", stagingTable);
            binding.setVariable("insertStmt", insertStmt);

			useStagingTable = true
	        binding.setVariable( "useStagingTable", useStagingTable )

            loadCmd.setFileLoc(stageTableName);

        }      


        AbstractSyntaxTree PRE_EXTRACT_TAP(SqlQuery sqlQuery) throws AdapterException,
                                                         oracle.odi.domain.mapping.exception.MappingException,
                                                         GenerationException {
             //TODO: Hack as the steps with subtypes="EXTRACT_TAP" are not generated.
             //They are generated only with subtypes="PRE_EXTRACT_TAP"  
             if (!isDirectLoad())                                       
                EXTRACT_TAP(sqlQuery);
             return super.PRE_EXTRACT_TAP(sqlQuery);

      }


      AbstractSyntaxTree EXTRACT_TAP(SqlQuery sqlQuery) throws AdapterException,
                                                         oracle.odi.domain.mapping.exception.MappingException,
                                                         GenerationException {

           PigScript pigScript = super.EXTRACT_TAP(sqlQuery);                                                         

           if (!isDirectLoad()) 
               createStagingTable(sqlQuery, pigScript);  

           return pigScript;
        }

    }

    public class StagingTable extends AbstractSyntaxTree {

        private String tableName;
        private String colDdlList;

        public StagingTable(String tableName, String colListDdl) {

           this.tableName = tableName;
           colDdlList = colListDdl;
        }

        public String tableName() {
          return tableName;
        }

        public String colListDdl() {
          return colDdlList;
        }

        public String tableOptions() {
           return ""; 
        }

        public String external() {
          return "";
        }

        public String ifNotExists() {
          return "";
        }

        public String partitionedClause() {
          return "";
        }

        public String clusteredClause() {
          return "";
        }

        public String sortByClause() {
          return "";
        }

        public String intoBucketsClause() {
          return "";
        }

        public String rowFormatClause() {
          return "";
        }

        public void resetReferencePath() {

        }

        public AbstractSyntaxTree combineTree(AbstractSyntaxTree tree) throws GenerationException {
           return this;
        }

        public String getType() {
           return "PigHiveStagingTable";
        }

        public List<Expression> getExpressions() {
           return null;
        }

        public void addPathPoint(MapConnectorPoint point) {

        }

        public AbstractSyntaxTree cloneAST() throws GenerationException {
            StagingTable stagingTable = new StagingTable(tableName);
            stagingTable.colDdlList = colDdlList;
            return stagingTable;
        }

        public List<AbstractSyntaxTree> getChildren() {
            List children = new ArrayList(0);
            return children;
        } 

        public Map<String,Object> getTemplateSubstitutionMap() {
          return new LinkedHashMap<String,Object>();
        }


    }

    public class HiveInsertStatement extends StagingTable {

        private SqlQuery selectQuery;

        public HiveInsertStatement(String tableName, String colListDdl, SqlQuery selectQuery) {
            super(tableName, colListDdl);
            this.selectQuery = selectQuery;
        }

        public SqlQuery selectQuery() {
            return selectQuery;
        }

        public String overwrite() {
            return "INTO";
        }

        public String partitionClause() {
           return "";
        }

    }


skipTaskGeneration = !useStagingTable;
if (!skipTaskGeneration) { 
  INSERT = insertStmt;
